# Load private keys from a text file
with open('private_keys.txt', 'r') as f:
    private_keys = [line.strip() for line in f.readlines()]

# Load proxies from a text file
with open('proxies.txt', 'r') as f:
    proxies = [line.strip() for line in f.readlines()]

# Ensure both files have the same number of entries
if len(private_keys) != len(proxies):
    raise ValueError("The number of private keys must match the number of proxies.")



# Initialize a loop to process each wallet
for i in range(len(private_keys)):
    private_key = private_keys[i]
    proxy = f'http://{proxies[i]}'

    # Create a Web3 instance using the proxy
    web3 = Web3(Web3.HTTPProvider(RPC, request_kwargs={"proxies": {'https': proxy, 'http': proxy}}))
    web3.middleware_onion.inject(geth_poa_middleware, layer=0)

    # Check if connected
    if not web3.is_connected():
        print(f"Failed to connect to Ethereum network using proxy: {proxy}")
        continue

    # Replace with your account address
    account_address = web3.eth.account.from_key(private_key).address

    # Delegatee address and numerator (example values)
    delegatee_address = account_address  # Delegating to self
    numerator = 10000  # Example numerator (0x2710 in hexadecimal)

    # Create the tuple of partial delegations
    partial_delegations = [(delegatee_address, numerator)]

    contract = web3.eth.contract(address=contract_address, abi=abi)

    def send_transaction():
        # Prepare the transaction
        transaction = contract.functions.delegate(partial_delegations).build_transaction({
            'from': account_address,
            'nonce': web3.eth.get_transaction_count(account_address),
            'maxFeePerGas': web3.eth.gas_price * 10,
            'maxPriorityFeePerGas': int(web3.eth.gas_price * random.uniform(1.1, 1.3)),
        })

        transaction['gas'] = int(web3.eth.estimate_gas(transaction) * 1.1)

        # Sign the transaction
        signed_txn = web3.eth.account.sign_transaction(transaction, private_key)

        # Send the transaction
        txn_hash = web3.eth.send_raw_transaction(signed_txn.rawTransaction)

        # Wait for the transaction receipt
        txn_receipt = web3.eth.wait_for_transaction_receipt(txn_hash)

        print(f'Transaction successful with hash: {txn_receipt.transactionHash.hex()}')

    # Attempt to send the transaction, catching any errors
    try:
        send_transaction()
    except ValueError as e:
        print(f'Error sending transaction for wallet {account_address}: {e}')
